import Head from 'next/head';
import { useState, useEffect, useRef } from "react";


const useMousePosition = () => {
  const [mousePosition, setMousePosition] = useState({ x: null, y: null });

  const updateMousePosition = ev => {
    setMousePosition({ x: ev.clientX, y: ev.clientY });
  };

  useEffect(() => {
    window.addEventListener("mousemove", updateMousePosition);

    return () => window.removeEventListener("mousemove", updateMousePosition);
  }, []);

  return mousePosition;
};


export default function Home() {
  const [isPlaying, setIsPlaying] = useState(true);


  const videoRef = useRef(null);

  function playVideo() {
    videoRef.current.play();
  }

  function pauseVideo() {
    videoRef.current.pause();
  }


  const { x, y } = useMousePosition();

  // console.log(`x: ${x}, y: ${y}`);
  const degree = ((x + y)/8) % 360;
  const color1 = '#eaee44';
  const color2 = '#33d0ff';

  return (
    <div className="container">
      <Head>
        <title>Portfolio of Carisa Tong</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <video autoPlay muted loop ref={videoRef} className="backgroundVideo">
        <source src="/vid2.mp4" type="video/mp4" />
      </video>
      <div className="overlay" style={{"backgroundImage": `linear-gradient(${degree}deg, ${color1}, ${color2})`}}/>
      <div className="top">
        <h3>Video by <a href="http://www.mariobaltodano.com">Mario Baltodano</a></h3>
        <div className="button" onClick={() => this.setState(!isPlaying)}>{isPlaying ? 'PAUSE' : 'PLAY'}</div>
      </div>

      <div className="contentContainer">
        <div className="content">
          <h1 className="title">
            WORK IN PROGRESS
          </h1>
          <h2 className="description">
            Let&#39;s connect on <a href="https://www.linkedin.com/in/carisatong">LinkedIn</a>
          </h2>
        </div>
      </div>
    </div>
  )
}
